-- Create a new user and grant all privileges
CREATE USER 'mediavaultadmin'@'localhost' IDENTIFIED BY '0000';
GRANT ALL PRIVILEGES ON * . * TO 'mediavaultadmin'@'localhost';
FLUSH PRIVILEGES;

-- Create mediavault database
CREATE DATABASE mediavault;
USE mediavault;

CREATE TABLE users (
	username VARCHAR(100) NOT NULL,
    userID INT NOT NULL AUTO_INCREMENT,
    PRIMARY KEY (userID)
);

CREATE TABLE logins (
	usernameEnc VARCHAR(255),
    passwordEnc VARCHAR(255),
    PRIMARY KEY (usernameEnc)
);

CREATE TABLE mediacollections (
	id INT NOT NULL AUTO_INCREMENT,
    userID INT NOT NULL,
    PRIMARY KEY (id)
);

CREATE TABLE mediarelations (
	mediaID INT NOT NULL,
    mediaCollectionID INT NOT NULL
);

CREATE TABLE allmovies (
	id INT NOT NULL,
    title VARCHAR(255),
    releaseDate VARCHAR(20),
    overview VARCHAR(900),
    posterPath VARCHAR(100),
    genre VARCHAR(50),
    runtime INT,
    PRIMARY KEY(id)
);







