DROP DATABASE mediavault;
CREATE DATABASE mediavault;
USE mediavault;

CREATE TABLE users (
	username VARCHAR(100) NOT NULL,
    userID INT NOT NULL AUTO_INCREMENT,
    PRIMARY KEY (userID)
);

CREATE TABLE logins (
	usernameEnc VARCHAR(255),
    passwordEnc VARCHAR(255),
    PRIMARY KEY (usernameEnc)
);

CREATE TABLE mediacollections (
	id INT NOT NULL AUTO_INCREMENT,
    userID INT NOT NULL,
    FOREIGN KEY (userID) REFERENCES users(userID),
    PRIMARY KEY (id)
);

CREATE TABLE mediarelations (
	mediaID INT NOT NULL AUTO_INCREMENT,
    mediaCollectionID INT NOT NULL,
    FOREIGN KEY (mediaCollectionID) REFERENCES mediacollections(id),
    PRIMARY KEY (mediaID)
);

CREATE TABLE movies (
	id INT NOT NULL,
    title VARCHAR(255),
    releaseDate DATE,
    image VARCHAR(255),
    genre VARCHAR(100),
    PRIMARY KEY(id)
);


